CREATE TABLE IF NOT EXISTS `bad_entity` (   id int NOT NULL PRIMARY KEY AUTO_INCREMENT,   number VARCHAR(1000) DEFAULT NULL,   name VARCHAR(1000) DEFAULT NULL,   confession VARCHAR(1000) DEFAULT NULL,   address VARCHAR(1000) DEFAULT NULL,   bad_fact_date VARCHAR(1000) DEFAULT NULL,   head_of VARCHAR(1000) DEFAULT NULL,   bin VARCHAR(1000) DEFAULT NULL,   bad_start_state VARCHAR(1000) DEFAULT NULL);
CREATE TEMPORARY TABLE `bad_entity_temp` (   `number`  VARCHAR(1000) DEFAULT NULL,   `name`  VARCHAR(1000) DEFAULT NULL,   `confession`  VARCHAR(1000) DEFAULT NULL,   `address`  VARCHAR(1000) DEFAULT NULL,   `bad_fact_date`  VARCHAR(1000) DEFAULT NULL,   `head_of`  VARCHAR(1000) DEFAULT NULL,   `bin`  VARCHAR(1000) DEFAULT NULL,   `bad_start_state`  VARCHAR(1000) DEFAULT NULL) DEFAULT CHARSET=utf8;
LOAD DATA LOCAL INFILE 'interprises_parsers/tmp/bad_entity.csv' IGNORE INTO TABLE bad_entity_temp FIELDS TERMINATED BY '\t' ENCLOSED BY '"' ESCAPED BY '\\' LINES TERMINATED BY '\n' IGNORE 1 LINES (   number, name, confession, address, bad_fact_date, head_of, bin, bad_start_state);
INSERT INTO bad_entity (   number, name, confession, address, bad_fact_date, head_of, bin, bad_start_state)   SELECT     number, name, confession, address, bad_fact_date, head_of, bin, bad_start_state  FROM bad_entity_temp ON DUPLICATE KEY UPDATE  number = VALUES(number), name = VALUES(name), confession = VALUES(confession), address = VALUES(address), bad_fact_date = VALUES(bad_fact_date), head_of = VALUES(head_of), bin = VALUES(bin), bad_start_state = VALUES(bad_start_state);