CREATE TABLE IF NOT EXISTS `bankrot_entity` ( id int NOT NULL PRIMARY KEY AUTO_INCREMENT,  number VARCHAR(1000) DEFAULT NULL,  region VARCHAR(1000) DEFAULT NULL,  name VARCHAR(1000) DEFAULT NULL,  bin_iin VARCHAR(1000) DEFAULT NULL,  rnn VARCHAR(1000) DEFAULT NULL,  address VARCHAR(1000) DEFAULT NULL,  ceo_fio VARCHAR(1000) DEFAULT NULL,  ceo_iin VARCHAR(1000) DEFAULT NULL,  founder_fio VARCHAR(1000) DEFAULT NULL,  founder_bin VARCHAR(1000) DEFAULT NULL,  activity VARCHAR(1000) DEFAULT NULL,  bankrot_start_date VARCHAR(1000) DEFAULT NULL);
CREATE TEMPORARY TABLE `bankrot_entity_temp` (   `number` VARCHAR(1000) DEFAULT NULL,  `region` VARCHAR(1000) DEFAULT NULL,  `name` VARCHAR(1000) DEFAULT NULL,  `bin_iin` VARCHAR(1000) DEFAULT NULL,  `rnn` VARCHAR(1000) DEFAULT NULL,  `address` VARCHAR(1000) DEFAULT NULL,  `ceo_fio` VARCHAR(1000) DEFAULT NULL,  `ceo_iin` VARCHAR(1000) DEFAULT NULL,  `founder_fio` VARCHAR(1000) DEFAULT NULL,  `founder_bin` VARCHAR(1000) DEFAULT NULL,  `activity` VARCHAR(1000) DEFAULT NULL,  `bankrot_start_date` VARCHAR(1000) DEFAULT NULL) DEFAULT CHARSET=utf8;
LOAD DATA LOCAL INFILE 'interprises_parsers/tmp/bankrot_entity.csv' IGNORE INTO TABLE bankrot_entity_temp FIELDS TERMINATED BY '\t' ENCLOSED BY '"' ESCAPED BY '\\' LINES TERMINATED BY '\n' IGNORE 1 LINES (  number,  region,  name,  bin_iin,  rnn,  address,  ceo_fio,  ceo_iin,  founder_fio,  founder_bin,  activity,  bankrot_start_date);
INSERT INTO bankrot_entity (  number,  region,  name,  bin_iin,  rnn,  address,  ceo_fio,  ceo_iin,  founder_fio,  founder_bin,  activity,  bankrot_start_date)   SELECT   number,  region,  name,  bin_iin,  rnn,  address,  ceo_fio,  ceo_iin,  founder_fio,  founder_bin,  activity,  bankrot_start_date FROM bankrot_entity_temp ON DUPLICATE KEY UPDATE    number = VALUES(number),  region = VALUES(region),  name = VALUES(name),  bin_iin = VALUES(bin_iin),  rnn = VALUES(rnn),  address = VALUES(address),  ceo_fio = VALUES(ceo_fio),  ceo_iin = VALUES(ceo_iin),  founder_fio = VALUES(founder_fio),  founder_bin = VALUES(founder_bin),  activity = VALUES(activity),  bankrot_start_date = VALUES(bankrot_start_date);