CREATE TABLE IF NOT EXISTS `good_entity` (    id int NOT NULL PRIMARY KEY AUTO_INCREMENT,    number VARCHAR(1000) DEFAULT NULL,    name VARCHAR(1000) DEFAULT NULL,    confession VARCHAR(1000) DEFAULT NULL,    address VARCHAR(1000) DEFAULT NULL,    bin VARCHAR(1000) DEFAULT NULL,    good_start_state VARCHAR(1000) DEFAULT NULL);
CREATE TEMPORARY TABLE `good_entity_temp` (    `number`  VARCHAR(1000) DEFAULT NULL,    `name`  VARCHAR(1000) DEFAULT NULL,    `confession`  VARCHAR(1000) DEFAULT NULL,    `address`  VARCHAR(1000) DEFAULT NULL,    `bin`  VARCHAR(1000) DEFAULT NULL,    `good_start_state`  VARCHAR(1000) DEFAULT NULL) DEFAULT CHARSET=utf8;
LOAD DATA LOCAL INFILE 'interprises_parsers/tmp/good_entity.csv' IGNORE INTO TABLE good_entity_temp FIELDS TERMINATED BY '\t' ENCLOSED BY '"' ESCAPED BY '\\' LINES TERMINATED BY '\n' IGNORE 1 LINES (    number,  name,  confession,  address,  bin,  good_start_state);
INSERT INTO good_entity (    number,  name,  confession,  address,  bin,  good_start_state)    SELECT      number,  name,  confession,  address,  bin,  good_start_state   FROM good_entity_temp ON DUPLICATE KEY UPDATE   number = VALUES(number),  name = VALUES(name),  confession = VALUES(confession),  address = VALUES(address),  bin = VALUES(bin),  good_start_state = VALUES(good_start_state);