CREATE TABLE IF NOT EXISTS `promiser_entity` (`BIN` VARCHAR(1000) DEFAULT NULL, `CEO` VARCHAR(1000) DEFAULT NULL);
CREATE TEMPORARY TABLE `promiser_entity_temp` ( `BIN` VARCHAR(1000) DEFAULT NULL, `CEO` VARCHAR(1000) DEFAULT NULL) DEFAULT CHARSET=utf8;
LOAD DATA LOCAL INFILE 'interprises_parsers/tmp/promisers.csv' IGNORE INTO TABLE promiser_entity_temp FIELDS TERMINATED BY '\t' ENCLOSED BY '"' ESCAPED BY '\\' LINES TERMINATED BY '\n' IGNORE 1 LINES (`BIN`, `CEO`);
INSERT INTO promiser_entity ( `BIN`, `CEO`) SELECT `BIN`, `CEO` FROM promiser_entity_temp ON DUPLICATE KEY UPDATE `BIN` = VALUES(`BIN`), `CEO` = VALUES(`CEO`);