CREATE TABLE IF NOT EXISTS `terror_entity` (  id int NOT NULL PRIMARY KEY AUTO_INCREMENT,  number VARCHAR(1000) DEFAULT NULL,  name VARCHAR(1000) DEFAULT NULL,  birthday VARCHAR(1000) DEFAULT NULL,  iin VARCHAR(1000) DEFAULT NULL,  notes VARCHAR(1000) DEFAULT NULL,  notes_fix VARCHAR(1000) DEFAULT NULL);
CREATE TEMPORARY TABLE `terror_entity_temp` (  `number` VARCHAR(1000) DEFAULT NULL,  `name` VARCHAR(1000) DEFAULT NULL,  `birthday` VARCHAR(1000) DEFAULT NULL,  `iin` VARCHAR(1000) DEFAULT NULL,  `notes` VARCHAR(1000) DEFAULT NULL,  `notes_fix` VARCHAR(1000) DEFAULT NULL) DEFAULT CHARSET=utf8;
LOAD DATA LOCAL INFILE 'interprises_parsers/tmp/terror_entity.csv' IGNORE INTO TABLE terror_entity_temp FIELDS TERMINATED BY '\t' ENCLOSED BY '"' ESCAPED BY '\\' LINES TERMINATED BY '\n' IGNORE 1 LINES (  number, name, birthday, iin, notes, notes_fix);
INSERT INTO terror_entity (  number, name, birthday, iin, notes, notes_fix )  SELECT    number, name, birthday, iin, notes, notes_fix FROM terror_entity_temp ON DUPLICATE KEY UPDATE   number = VALUES(number), name = VALUES(name), birthday = VALUES(birthday), iin = VALUES(iin), notes = VALUES(notes), notes_fix = VALUES(notes_fix);